package com.example.loginfire;

import android.app.ProgressDialog;
import android.content.Intent;
import android.os.Bundle;
import android.text.Editable;
import android.text.TextWatcher;
import android.view.View;
import android.widget.AdapterView;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ListView;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.firestore.FirebaseFirestore;

import java.util.ArrayList;
import java.util.List;

public class MainActivity extends AppCompatActivity {
    private FirebaseAuth auth;
    private FirebaseFirestore firestore;
    private ProgressDialog progress;
    private Button buttonLogout, buttonFilter;
    private EditText editTextSearch;
    private ListView listViewProdutos;
    private ProdutoAdapter adapter;
    private List<Produto> produtos, produtosFiltrados;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_main);

        buttonLogout = findViewById(R.id.button_logout);
        buttonFilter = findViewById(R.id.button_filter);
        editTextSearch = findViewById(R.id.edit_text_search);
        listViewProdutos = findViewById(R.id.list_view_produtos);

        auth = FirebaseAuth.getInstance();
        firestore = FirebaseFirestore.getInstance();
        progress = new ProgressDialog(this);
        progress.setTitle("Saindo");
        progress.setMessage("Aguarde...");

        produtos = new ArrayList<>();
        produtos.add(new Produto("Xiaomi Note 6", "Descrição do Xiaomi Note 6", 1000.00, "data:image/jpeg;base64,"));
        produtos.add(new Produto("Xiaomi Note 7", "Descrição do Xiaomi Note 7", 1200.00, "data:image/jpeg;base64,"));
        produtos.add(new Produto("Xiaomi Note 8", "Descrição do Xiaomi Note 8", 1400.00, "data:image/jpeg;base64,"));
        produtos.add(new Produto("Xiaomi Note 9", "Descrição do Xiaomi Note 9", 1600.00, "data:image/jpeg;base64,"));
        produtos.add(new Produto("Xiaomi Note 10", "Descrição do Xiaomi Note 10", 1800.00, "https://m.media-amazon.com/images/I/51VTCbe-hDL._AC_UF1000,1000_QL80_.jpg"));
        produtos.add(new Produto("Xiaomi Note 11", "Descrição do Xiaomi Note 11", 2000.00, "https://m.media-amazon.com/images/I/51VTCbe-hDL._AC_UF1000,1000_QL80_.jpg"));
        produtos.add(new Produto("Xiaomi Note 12", "Descrição do Xiaomi Note 12", 2200.00, "https://cdn.awsli.com.br/600x700/1745/1745279/produto/223317658/celular_xiaomi_redmi_note_12_pro_dual_chip_256gb_4g_global_158101_550x550-8aagjdxuv1.jpg"));

        produtosFiltrados = new ArrayList<>(produtos);

        adapter = new ProdutoAdapter(this, produtosFiltrados);
        listViewProdutos.setAdapter(adapter);

        listViewProdutos.setOnItemClickListener(new AdapterView.OnItemClickListener() {
            @Override
            public void onItemClick(AdapterView<?> parent, View view, int position, long id) {
                Produto produtoSelecionado = produtosFiltrados.get(position);
                Toast.makeText(MainActivity.this, "Produto selecionado: " + produtoSelecionado.getNome(), Toast.LENGTH_SHORT).show();
            }
        });

        buttonLogout.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                auth.signOut();
                progress.show();
                Intent intent = new Intent(MainActivity.this, LoginActivity.class);
                intent.setFlags(Intent.FLAG_ACTIVITY_CLEAR_TASK | Intent.FLAG_ACTIVITY_NEW_TASK);
                startActivity(intent);
                finish();
            }
        });

        editTextSearch.addTextChangedListener(new TextWatcher() {
            @Override
            public void beforeTextChanged(CharSequence s, int start, int count, int after) {}

            @Override
            public void onTextChanged(CharSequence s, int start, int before, int count) {
                filtrarProdutos(s.toString());
            }

            @Override
            public void afterTextChanged(Editable s) {}
        });

        buttonFilter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Implementar lógica de filtro se necessário
            }
        });

        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });
    }

    private void filtrarProdutos(String query) {
        produtosFiltrados.clear();
        for (Produto produto : produtos) {
            if (produto.getNome().toLowerCase().contains(query.toLowerCase())) {
                produtosFiltrados.add(produto);
            }
        }
        adapter.notifyDataSetChanged();
    }
}
